-- Drop the existing table
DROP TABLE IF EXISTS Technologies;
-- Create the new Table
CREATE TABLE Technologies ('ID' integer primary key autoincrement , 
								'Type' text , 
								'Description' text default NULL , 
								'Civilopedia' text default NULL , 
								'Help' text default NULL , 
								'AIWeight' integer default 0 , 
								'AITradeModifier' integer default 0 , 
								'Cost' integer default 0 , 
								'AdvancedStartCost' integer default '-1' , 
								'Era' text default NULL , 
								'FirstFreeUnitClass' text default NULL , 
								'FeatureProductionModifier' integer default 0 , 
								'WorkerSpeedModifier' integer default 0 , 
								'FirstFreeTechs' integer default 0 , 
								'EmbarkedMoveChange' integer default 0 , 
								'EndsGame' boolean default 0 , 
								'AllowsEmbarking' boolean default 0 , 
								'AllowsDefensiveEmbarking' boolean default 0 , 
								'EmbarkedAllWaterPassage' boolean default 0 , 
								'AllowsBarbarianBoats' boolean default 0 , 
								'Repeat' boolean default 0 , 
								'Trade' boolean default 0 , 
								'Disable' boolean default 0 , 
								'GoodyTech' boolean default 0 , 
								'ExtraWaterSeeFrom' boolean default 0 , 
								'MapCentering' boolean default 0 , 
								'MapVisible' boolean default 0 , 
								'MapTrading' boolean default 0 , 
								'TechTrading' boolean default 0 , 
								'GoldTrading' boolean default 0 , 
								'AllowEmbassyTradingAllowed' boolean default 0 , 
								'OpenBordersTradingAllowed' boolean default 0 , 
								'DefensivePactTradingAllowed' boolean default 0 , 
								'ResearchAgreementTradingAllowed' boolean default 0 , 
								'TradeAgreementTradingAllowed' boolean default 0 , 
								'PermanentAllianceTradingAllowed' boolean default 0 , 
								'BridgeBuilding' boolean default 0 , 
								'WaterWork' boolean default 0 , 
								'GridX' integer default 0 , 
								'GridY' integer default 0 , 
								'Quote' text default NULL , 
								'PortraitIndex' integer default '-1' , 
								'IconAtlas' text default NULL , 
								'AudioIntro' text default NULL , 
								'AudioIntroHeader' text default NULL , 
								'TechCat' TEXT, foreign key (IconAtlas) references IconTextureAtlases(Atlas));
